mapper.select:
select
  memo_id, title, content, status, created_by, creation_date, last_update_by, last_update_date
from memo


        and memo_id = #{memoId}


        and title = #{title}


        and content = #{content}


        and status = #{status}


        and created_by = #{createdBy}


        and creation_date = #{creationDate}


        and last_update_by = #{lastUpdateBy}


        and last_update_date = #{lastUpdateDate}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from memo


        and memo_id = #{memoId}


        and title = #{title}


        and content = #{content}


        and status = #{status}


        and created_by = #{createdBy}


        and creation_date = #{creationDate}


        and last_update_by = #{lastUpdateBy}


        and last_update_date = #{lastUpdateDate}
