mapper.select:
select
  memo_id, tag_id, created_by, creation_date, last_update_by, last_update_date
from memo_tag


        and memo_id = #{memoId}


        and tag_id = #{tagId}


        and created_by = #{createdBy}


        and creation_date = #{creationDate}


        and last_update_by = #{lastUpdateBy}


        and last_update_date = #{lastUpdateDate}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from memo_tag


        and memo_id = #{memoId}


        and tag_id = #{tagId}


        and created_by = #{createdBy}


        and creation_date = #{creationDate}


        and last_update_by = #{lastUpdateBy}


        and last_update_date = #{lastUpdateDate}
