mapper.select:
select
  reminder_id, user_id, title, content, remind_time, created_by, creation_date, last_updated_by, last_update_date
from reminder


        and reminder_id = #{reminderId}


        and user_id = #{userId}


        and title = #{title}


        and content = #{content}


        and remind_time = #{remindTime}


        and created_by = #{createdBy}


        and creation_date = #{creationDate}


        and last_updated_by = #{lastUpdatedBy}


        and last_update_date = #{lastUpdateDate}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from reminder


        and reminder_id = #{reminderId}


        and user_id = #{userId}


        and title = #{title}


        and content = #{content}


        and remind_time = #{remindTime}


        and created_by = #{createdBy}


        and creation_date = #{creationDate}


        and last_updated_by = #{lastUpdatedBy}


        and last_update_date = #{lastUpdateDate}
