mapper.select:
select
  tag_id, name, created_by, creation_date, last_update_by, last_update_date
from tag


        and tag_id = #{tagId}


        and name = #{name}


        and created_by = #{createdBy}


        and creation_date = #{creationDate}


        and last_update_by = #{lastUpdateBy}


        and last_update_date = #{lastUpdateDate}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tag


        and tag_id = #{tagId}


        and name = #{name}


        and created_by = #{createdBy}


        and creation_date = #{creationDate}


        and last_update_by = #{lastUpdateBy}


        and last_update_date = #{lastUpdateDate}
